INSERT INTO public.vocabularies (code, accessible_at, description, label) VALUES ('tadirah-research-activity', NULL, 'Research activities are usually applied to one or several research objects. An article about modeling of manuscript properties would therefore be tagged with the tags "Modeling" and "Manuscript". A plain text editor would be tagged with the tags "Writing" and "Code" and "Text".', 'TaDiRAH Research Activity');
